client/src/hooks/use-elevenlabs.tsx.content:
import { useState, useCallback } from "react";

interface ElevenLabsOptions {
  onEnd?: () => void;
  onError?: (error: string) => void;
  voiceId?: string;
  modelId?: string;
  stability?: number;
  similarityBoost?: number;
}

interface UseElevenLabsReturn {
  speak: (text: string, options?: ElevenLabsOptions) => Promise<void>;
  stop: () => void;
  isSpeaking: boolean;
  isLoading: boolean;
}

export function useElevenLabs(): UseElevenLabsReturn {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [currentAudio, setCurrentAudio] = useState<HTMLAudioElement | null>(null);

  // Female voices for consistency - using user specified voice
  const VOICES = {
    hindi: "iWNf11sz1GrUE4ppxTOL", // User specified female voice for Hindi
    english: "iWNf11sz1GrUE4ppxTOL", // User specified female voice for English
  };

  const speak = useCallback(async (text: string, options: ElevenLabsOptions = {}) => {
    console.log('TTS: Starting speech for:', text.substring(0, 50));

    // Try ElevenLabs API first, fallback to Web Speech API with female voice
    try {
      setIsLoading(true);

      // Stop any current audio
      if (currentAudio) {
        currentAudio.pause();
        currentAudio.currentTime = 0;
      }

      // Try ElevenLabs first
      try {
        const voiceId = options.voiceId || VOICES.english;

        const response = await fetch('/api/tts', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            text: text,
            voiceId: voiceId,
            modelId: options.modelId || "eleven_monolingual_v1",
            stability: options.stability || 0.5,
            similarityBoost: options.similarityBoost || 0.5
          })
        });

        if (response.ok) {
          const audioBlob = await response.blob();
          const audioUrl = URL.createObjectURL(audioBlob);
          const audio = new Audio(audioUrl);

          setCurrentAudio(audio);
          setIsLoading(false);
          setIsSpeaking(true);

          console.log('TTS: ElevenLabs audio ready, playing...');

          audio.onended = () => {
            setIsSpeaking(false);
            setCurrentAudio(null);
            URL.revokeObjectURL(audioUrl);
            console.log('TTS: ElevenLabs audio finished');
            options.onEnd?.();
          };

          audio.onerror = () => {
            setIsSpeaking(false);
            setIsLoading(false);
            setCurrentAudio(null);
            URL.revokeObjectURL(audioUrl);
            console.error('TTS: Audio playback failed');
            options.onError?.("Audio playback failed");
          };

          await audio.play();
          return; // Success with ElevenLabs
        } else {
          const errorData = await response.json().catch(() => ({ message: 'Unknown error' }));
          throw new Error(errorData.message || `HTTP ${response.status}`);
        }
      } catch (elevenlabsError) {
        console.warn('ElevenLabs failed, falling back to Web Speech API:', elevenlabsError instanceof Error ? elevenlabsError.message : 'Unknown error');
      }

      // Fallback to Web Speech API with female voice
      if ('speechSynthesis' in window) {
        window.speechSynthesis.cancel();

        const utterance = new SpeechSynthesisUtterance(text);
        utterance.rate = 0.9;
        utterance.pitch = 1.2; // Higher pitch for more feminine sound
        utterance.volume = 1.0;
        utterance.lang = 'en-US';

        // Try to get a female voice
        const voices = speechSynthesis.getVoices();
        const femaleVoice = voices.find(voice => 
          voice.name.toLowerCase().includes('female') ||
          voice.name.toLowerCase().includes('woman') ||
          voice.name.toLowerCase().includes('google us english female') ||
          voice.name.toLowerCase().includes('samantha') ||
          voice.name.toLowerCase().includes('karen')
        );

        if (femaleVoice) {
          utterance.voice = femaleVoice;
        }

        setIsLoading(false);
        setIsSpeaking(true);

        utterance.onend = () => {
          setIsSpeaking(false);
          options.onEnd?.();
        };

        utterance.onerror = (event) => {
          setIsSpeaking(false);
          setIsLoading(false);
          options.onError?.(`Speech failed: ${event.error}`);
        };

        speechSynthesis.speak(utterance);
        return;
      }

      throw new Error('No speech synthesis available');

    } catch (error) {
      setIsSpeaking(false);
      setIsLoading(false);
      setCurrentAudio(null);
      console.error('All TTS methods failed:', error);
      options.onError?.(error instanceof Error ? error.message : "Speech synthesis failed");
    }
  }, [currentAudio]);

  const stop = useCallback(() => {
    if (currentAudio) {
      currentAudio.pause();
      currentAudio.currentTime = 0;
      setCurrentAudio(null);
    }
    // Stop Web Speech API as well
    if ('speechSynthesis' in window) {
      window.speechSynthesis.cancel();
    }
    setIsSpeaking(false);
    setIsLoading(false);
  }, [currentAudio]);

  return {
    speak,
    stop,
    isSpeaking,
    isLoading,
  };
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-speech.tsx.content:
import { useState, useCallback, useRef } from "react";

interface SpeechOptions {
  onEnd?: () => void;
  onError?: (error: string) => void;
  voice?: SpeechSynthesisVoice;
  rate?: number;
  pitch?: number;
  volume?: number;
}

interface UseSpeechReturn {
  speak: (text: string, options?: SpeechOptions) => void;
  stop: () => void;
  isSpeaking: boolean;
  supported: boolean;
  voices: SpeechSynthesisVoice[];
}

export function useSpeech(): UseSpeechReturn {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voices, setVoices] = useState<SpeechSynthesisVoice[]>([]);
  const utteranceRef = useRef<SpeechSynthesisUtterance | null>(null);

  const supported = typeof window !== 'undefined' && 'speechSynthesis' in window;

  // Load available voices
  const loadVoices = useCallback(() => {
    if (!supported) return;

    const availableVoices = speechSynthesis.getVoices();
    setVoices(availableVoices);
  }, [supported]);

  // Initialize voices on component mount
  useState(() => {
    if (supported) {
      loadVoices();
      speechSynthesis.addEventListener('voiceschanged', loadVoices);
      return () => {
        speechSynthesis.removeEventListener('voiceschanged', loadVoices);
      };
    }
  });

  const speak = useCallback((text: string, options: SpeechOptions = {}) => {
    if (!supported || !text.trim()) return;

    // Stop any current speech
    speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(text);
    utteranceRef.current = utterance;

    // Detect language and set appropriate voice
    const isHindi = /[\u0900-\u097F]/.test(text);
    const preferredVoices = voices.filter(voice => 
      isHindi 
        ? voice.lang.includes('hi') || voice.lang.includes('Hindi')
        : voice.lang.includes('en')
    );

    if (preferredVoices.length > 0) {
      utterance.voice = options.voice || preferredVoices[0];
    }

    // Set speech parameters
    utterance.rate = options.rate || 0.9;
    utterance.pitch = options.pitch || 1;
    utterance.volume = options.volume || 1;
    utterance.lang = isHindi ? 'hi-IN' : 'en-US';

    // Event handlers
    utterance.onstart = () => {
      setIsSpeaking(true);
    };

    utterance.onend = () => {
      setIsSpeaking(false);
      options.onEnd?.();
    };

    utterance.onerror = (event) => {
      setIsSpeaking(false);
      options.onError?.(event.error);
    };

    // Speak
    speechSynthesis.speak(utterance);
  }, [supported, voices]);

  const stop = useCallback(() => {
    if (!supported) return;

    speechSynthesis.cancel();
    setIsSpeaking(false);
  }, [supported]);

  return {
    speak,
    stop,
    isSpeaking,
    supported,
    voices,
  };
}

