import { useEffect, useState } from "react";
import { motion } from "framer-motion";

interface WaveformAnimationProps {
  isListening: boolean;
  audioLevel?: number;
  isSpeaking?: boolean;
  isActive?: boolean;
}

export function WaveformAnimation({ isListening, audioLevel = 0.5, isSpeaking = false, isActive = true }: WaveformAnimationProps) {
  const [bars] = useState(() => Array.from({ length: 4 }, (_, i) => i));
  const [animationTime, setAnimationTime] = useState(0);

  // Update animation time for smooth animations using RAF
  useEffect(() => {
    if (!isListening && !isSpeaking) return;

    let animationId: number;
    const updateTime = () => {
      setAnimationTime(Date.now());
      animationId = requestAnimationFrame(updateTime);
    };

    animationId = requestAnimationFrame(updateTime);

    return () => cancelAnimationFrame(animationId);
  }, [isListening, isSpeaking]);

  const getBarHeight = (index: number) => {
    if (!isListening && !isSpeaking) return 32;

    // Create unique timing for each bar with different frequencies and phases
    const baseHeight = 32;
    const maxHeight = isSpeaking ? 90 : 70;

    // More dramatic movement with different frequencies and phases for each bar
    const frequencies = [0.015, 0.022, 0.028, 0.018]; // Faster speeds for more movement
    const phases = [0, 2.1, 4.2, 1.4]; // Different starting points
    const amplitudes = [0.8, 1.0, 0.9, 1.1]; // Higher intensities for more movement

    const frequency = frequencies[index % frequencies.length];
    const phase = phases[index % phases.length];
    const amplitude = amplitudes[index % amplitudes.length];

    // Add secondary wave for more natural voice-like movement
    const primaryWave = Math.sin(animationTime * frequency + phase) * amplitude;
    const secondaryWave = Math.sin(animationTime * frequency * 1.3 + phase * 0.7) * 0.3;
    const variation = (primaryWave + secondaryWave) * 0.5 + 0.7;

    return baseHeight + (maxHeight - baseHeight) * audioLevel * Math.max(0.3, variation);
  };

  const barVariants = {
    idle: (index: number) => ({
      height: 32,
      backgroundColor: "hsl(220 8% 65%)", // Soft gray
      transition: { 
        duration: 0.5,
        type: "spring",
        damping: 20,
        stiffness: 100
      }
    }),
    listening: (index: number) => ({
      height: getBarHeight(index),
      backgroundColor: "hsl(210 60% 70%)", // Gentle blue
      transition: {
        height: {
          duration: 0.2,
          ease: "easeInOut",
          type: "tween"
        }
      }
    }),
    speaking: (index: number) => ({
      height: getBarHeight(index),
      backgroundColor: "hsl(210 45% 60%)", // Professional blue-gray
      transition: {
        height: {
          duration: 0.08 + (index * 0.015), // Faster, more responsive timing
          ease: "easeOut",
          type: "tween"
        }
      }
    })
  };

  const getAnimationState = () => {
    if (isSpeaking) return "speaking";
    if (isListening) return "listening";
    return "idle";
  };

  return (
    <div 
      className={`flex items-center justify-center space-x-2 transition-opacity duration-300 ${
        isActive ? 'opacity-100' : 'opacity-30'
      }`}
      style={{ 
        willChange: 'auto',
        contain: 'layout',
        transform: 'translateZ(0)',
        height: '80px',
        display: 'flex',
        alignItems: 'center'
      }}
      data-testid="waveform-animation"
    >
      {bars.map((index) => (
        <motion.div
          key={index}
          custom={index}
          variants={barVariants}
          animate={getAnimationState()}
          className="w-4 rounded-full"
          style={{
            minHeight: 32,
            maxHeight: 80, // Prevent bars from growing beyond container
            filter: "drop-shadow(0 4px 12px rgba(71, 85, 105, 0.25)) drop-shadow(0 0 8px rgba(71, 85, 105, 0.15))",
            willChange: 'height',
            transform: 'translateZ(0)',
            contain: 'layout'
          }}
        />
      ))}
    </div>
  );
}

client/src/hooks/use-three-tier-tts.tsx.content:
import { useState, useCallback } from "react";

interface TTSOptions {
  onStart?: () => void;
  onEnd?: () => void;
  onError?: (error: string) => void;
  // ElevenLabs parameters
  voiceId?: string;
  modelId?: string;
  stability?: number;
  similarityBoost?: number;
  // Cartesia parameters
  cartesiaModelId?: string;
  speed?: "slowest" | "slow" | "normal" | "fast" | "fastest" | number;
  emotions?: string[];
  language?: "en" | "fr" | "de" | "es" | "pt" | "zh" | "ja" | "hi" | "it" | "ko" | "nl" | "pl" | "ru" | "sv" | "tr";
}

interface UseThreeTierTTSReturn {
  speak: (text: string, options?: TTSOptions) => Promise<void>;
  stop: () => void;
  isSpeaking: boolean;
  isLoading: boolean;
}

export function useThreeTierTTS(): UseThreeTierTTSReturn {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [currentAudio, setCurrentAudio] = useState<HTMLAudioElement | null>(null);

  // Female voices for consistency - using user specified voice
  const VOICES = {
    hindi: "iWNf11sz1GrUE4ppxTOL", // User specified female voice for Hindi
    english: "iWNf11sz1GrUE4ppxTOL", // User specified female voice for English
  };

  const speak = useCallback(async (text: string, options: TTSOptions = {}) => {
    console.log('TTS: Starting three-tier speech for:', text.substring(0, 50));

    // Three-tier system: Cartesia -> ElevenLabs -> Browser Speech API
    try {
      setIsLoading(true);

      // Stop any current audio
      if (currentAudio) {
        currentAudio.pause();
        currentAudio.currentTime = 0;
      }

      // Try API providers (Cartesia first, then ElevenLabs)
      try {
        const voiceId = options.voiceId || VOICES.english;

        const response = await fetch('/api/tts', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            text: text,
            voiceId: voiceId,
            // ElevenLabs parameters
            modelId: options.modelId || "eleven_multilingual_v2",
            stability: options.stability || 0.6,
            similarityBoost: options.similarityBoost || 0.8,
            // Cartesia parameters
            cartesiaModelId: options.cartesiaModelId || "sonic-2",
            speed: options.speed || "normal",
            emotions: options.emotions,
            language: options.language || "en"
          })
        });

        if (response.ok) {
          const audioBlob = await response.blob();
          const audioUrl = URL.createObjectURL(audioBlob);
          const audio = new Audio(audioUrl);

          const provider = response.headers.get('X-TTS-Provider') || 'unknown';

          setCurrentAudio(audio);
          setIsLoading(false);
          setIsSpeaking(true);

          console.log(`TTS: ${provider} audio ready, playing...`);

          audio.onplay = () => {
            console.log(`TTS: ${provider} audio started playing`);
            options.onStart?.();
          };

          audio.onended = () => {
            setIsSpeaking(false);
            setCurrentAudio(null);
            URL.revokeObjectURL(audioUrl);
            console.log(`TTS: ${provider} audio finished`);
            options.onEnd?.();
          };

          audio.onerror = () => {
            setIsSpeaking(false);
            setIsLoading(false);
            setCurrentAudio(null);
            URL.revokeObjectURL(audioUrl);
            console.error('TTS: Audio playback failed');
            options.onError?.("Audio playback failed");
          };

          await audio.play();
          return; // Success with API provider
        } else {
          const errorData = await response.json().catch(() => ({ message: 'Unknown error' }));

          // Check if we should fallback to browser
          if (errorData.fallbackToBrowser) {
            console.log('TTS: API providers unavailable, using browser fallback');
          } else {
            throw new Error(errorData.message || `HTTP ${response.status}`);
          }
        }
      } catch (apiError) {
        console.warn('TTS: API providers failed, falling back to Web Speech API:', apiError instanceof Error ? apiError.message : 'Unknown error');
      }

      // Fallback to Web Speech API with female voice
      if ('speechSynthesis' in window) {
        window.speechSynthesis.cancel();

        const utterance = new SpeechSynthesisUtterance(text);
        utterance.rate = 0.9;
        utterance.pitch = 1.2; // Higher pitch for more feminine sound
        utterance.volume = 1.0;
        utterance.lang = 'en-US';

        // Try to get a female voice
        const voices = speechSynthesis.getVoices();
        const femaleVoice = voices.find(voice => 
          voice.name.toLowerCase().includes('female') ||
          voice.name.toLowerCase().includes('woman') ||
          voice.name.toLowerCase().includes('google us english female') ||
          voice.name.toLowerCase().includes('samantha') ||
          voice.name.toLowerCase().includes('karen')
        );

        if (femaleVoice) {
          utterance.voice = femaleVoice;
        }

        setIsLoading(false);
        setIsSpeaking(true);

        utterance.onstart = () => {
          console.log('TTS: Web Speech API started speaking');
          options.onStart?.();
        };

        utterance.onend = () => {
          setIsSpeaking(false);
          options.onEnd?.();
        };

        utterance.onerror = (event) => {
          setIsSpeaking(false);
          setIsLoading(false);
          options.onError?.(`Speech failed: ${event.error}`);
        };

        speechSynthesis.speak(utterance);
        return;
      }

      throw new Error('No speech synthesis available');

    } catch (error) {
      setIsSpeaking(false);
      setIsLoading(false);
      setCurrentAudio(null);
      console.error('All TTS methods failed:', error);
      options.onError?.(error instanceof Error ? error.message : "Speech synthesis failed");
    }
  }, [currentAudio]);

  const stop = useCallback(() => {
    if (currentAudio) {
      currentAudio.pause();
      currentAudio.currentTime = 0;
      setCurrentAudio(null);
    }
    // Stop Web Speech API as well
    if ('speechSynthesis' in window) {
      window.speechSynthesis.cancel();
    }
    setIsSpeaking(false);
    setIsLoading(false);
  }, [currentAudio]);

  return {
    speak,
    stop,
    isSpeaking,
    isLoading,
  };
}

